const catalogSwiper1 = new Swiper('.catalog__swiper1', {
    speed: 1500,
    parallax: true,
    slidesPerView: "auto",
    navigation: {
      nextEl: '.catalog__swiper-arrow_next1',
      prevEl: '.catalog__swiper-arrow_prev1',
      disabledClass: 'catalog__swiper-arrow_disabled',
    },
  });

